$("#contactForm").validator().on("submit",function(a){if(a.isDefaultPrevented()){formError();submitMSG(false,"Did you fill in the form properly?")}else{a.preventDefault();submitForm()}});function submitForm(){var b=$("#name").val();var a=$("#email").val();var c=$("#message").val();$.ajax({type:"POST",url:"assets/php/form-process.php",data:"name="+b+"&email="+a+"&message="+c,success:function(d){if(d=="success"){formSuccess()}else{formError();submitMSG(false,d)}}})}function formSuccess(){$("#contactForm")[0].reset();submitMSG(true,"Message Submitted!")}function formError(){$("#contactForm").removeClass().addClass("shake animated").one("webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",function(){$(this).removeClass()})}function submitMSG(b,c){if(b){var a="h3 text-center tada animated text-success"}else{var a="h3 text-center text-danger"}$("#msgSubmit").removeClass().addClass(a).text(c)};